import React, {useState} from 'react';
import { createBlogEntry } from './BlogQueries';

const AddBlogEntryForm = props => {

    const initEntry = {id: null, title: "", content: "", img_link: ""};
    let [entry, setEntry] = useState(initEntry);
    
    let handleChange = e => {
        let name = e.target.name;
        setEntry({...entry, [name]: e.target.value});
    }

    let handleSubmit = e =>{
        //createBlogEntry(entry);
        props.addNewEntry(entry);
        setEntry(initEntry);
        e.preventDefault();
    }
    
    return(
        <form onSubmit={e=>{handleSubmit(e)}}>
                <input type="text" name="title" placeholder="Title" onChange={e=>{handleChange(e)}}></input>
                <input type="text" name="content" placeholder="content" onChange={e=>{handleChange(e)}}></input>
                <input type="text" name="img_link" placeholder="Image link" onChange={e=>{handleChange(e)}}></input>
                <button type="submit">Create</button>
        </form>
    );
}

export default AddBlogEntryForm;
