import React, {useState, useEffect} from 'react';
import { getBlogEntries } from './BlogQueries';
import AddBlogEntryForm from './AddBlogEntryForm';
import {getUsers} from '../users/UserQueries';
const BlogEntries = () => {

    let [blogEntries, setBlogEntries] = useState([]);
    
    const addNewEntry = entry => {
        setBlogEntries([...blogEntries, entry]);
    }
    
    useEffect(()=>{
        getBlogEntries().then(res => setBlogEntries(res));
    },[]);

    let displayName = userId => {
        let u = users[userId];
        return u.name 
    }

    return(
        <div className="blogContent">
            <div className="blogentryform">
            <AddBlogEntryForm addNewEntry={addNewEntry} users={users} />
            </div>
            <div className="bloglist">
            {users !== undefined || users.length === 0   ? (
                blogEntries.map(e => 
                
                    <div key={e.id} className="entries">
                        <div>{e.img_link}</div>
                        <div>{e.entry_name}</div>
                        <div>
                            <h2>{e.title}</h2>
                            {e.content}
                        </div>
                         
                    </div>
                )

            ):(
                <div>Loading</div>
            ) //FEEEEL PÅ USERS
        }
            
            </div>
        </div>
    );
}

export default BlogEntries;
