import React, {useState, useEffect} from 'react';
import AddUserForm from './AddUserForm';
import {getUsers} from './UserQueries';

const UserList = params => {
    
    const [users, setUsers] = useState([])

    const updateUsers = () => {
      getUsers().then(res => setUsers(res));
    }
  
    useEffect(() => {
      updateUsers();
    }, []);
    

    return(
        <div className="userList">
            <div className="row-1"><UserTable /></div>
            <div className="row-2"><AddUserForm updateUsers={updateUsers}/></div>
        </div>
        
    );
}

const UserTable = params => (
    <table>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>testaaaaaa</th>
                </tr>
            </thead>
            <tbody>
            {users.map(u => 
                <tr key={u.id}>
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>edit delete</td>
                </tr>    
            )}
            </tbody>
        </table>
);

export default UserList;
