import React, {useState, useEffect} from 'react';
import AddUserForm from './AddUserForm';
import EditUserForm from './EditUserForm';
import {getUsers, removeUser} from './UserQueries';

const UserList = () => {

    const [users, setUsers] = useState([]);
    const [edit, setEdit] = useState(false);
    const [user, setUser] = useState({});

    const addUser = newUser => {
        setUsers([...users, newUser]);
    }

    const updateUsers = updatedUser => {
      let newUsers = users.map(u => u.id === updatedUser.id ? updatedUser : u);
      setUsers(newUsers);
    }

    const handleEditUser = target => {
        setUser(target);
        setEdit(true);
    }

    const handleRemove = targetId => {
        removeUser(targetId);
        let newUsers = users.filter(u => u.id !== targetId);
        setUsers(newUsers);
    }

    useEffect(() => {
        getUsers().then(res => setUsers(res));
    }, []);
    

    return(
        <div className="userList">
            <div className="row-1">
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {users.map(u => 
                    <tr key={u.id}>
                    <td>{u.name}</td>
                    <td>{u.email}</td>
                    <td><button disabled="false" onClick={() => handleEditUser(u)}>Edit</button> <button onClick={() => { handleRemove(u.id)}}>Delete</button></td>
                    </tr>    
                )}
                </tbody>
            </table>    
            </div>
            <div className="row-2">
                {edit ? (
                 <EditUserForm user={user} updateUsers={updateUsers} setEdit={setEdit}/>
                ) : (
                 <AddUserForm addUser={addUser} />
                )}
            </div>
        </div>
        
    );
}


export default UserList;
