import React, {useState, useEffect} from 'react';
import AddUserForm from './AddUserForm';
import {getUsers} from './UserQueries';

const UserList = params => {
    
    const [users, setUsers] = useState([]);
    const [edit, setEdit] = useState(false);

    const updateUsers = () => {
      getUsers().then(res => setUsers(res));
    }
  
    useEffect(() => {
      updateUsers();
    }, []);
    

    return(
        <div className="userList">
            <div className="row-1">
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {users.map(u => 
                    <tr key={u.id}>
                    <td>{u.name}</td>
                    <td>{u.email}</td>
                    <td><button onClick={() => setEdit(true)}>Edit</button> <button>Delete</button></td>
                    </tr>    
                )}
                </tbody>
            </table>    
            </div>
            <div className="row-2"><AddUserForm updateUsers={updateUsers}/></div>
        </div>
        
    );
}


export default UserList;
